export interface P2PClientOptions {
    address: string;
    timeout?: number;
    binaryPath?: string;
    protoPath?: string;
}
export interface Message {
    messageId: string;
    fromAgentId: string;
    content: string;
    timestamp: number;
}
export type MessageHandler = (message: Message) => void;
export interface P2PNodeOptions {
    port?: number;
    agentId?: string;
}
export interface DHTRecord {
    agentName?: string;
    peerId: string;
    timestamp?: number;
}
export interface Agent {
    agent_id: string;
    agent_name: string;
    peer_id: string;
    connected_since: number;
}
export interface ListAgentsResponse {
    agents: Agent[];
}
